function OffsetSlider(){return document.querySelector(".swiper-container .swiper-slide").offsetWidth/2+16}var enableScroll=function(e,o){var i=document.querySelector(e),n=document.querySelector(o);i.onclick=function(){n.scrollIntoView({behavior:"smooth",block:"start"})}};window.onload=function(){enableScroll("#scroller","#section-join");new Swiper(".swiper-default",{slidesPerView:3,slidesOffsetBefore:window.onresize=OffsetSlider,spaceBetween:32,grabCursor:!0,loop:!0,autoplay:{delay:2500,disableOnInteraction:!1},pagination:{el:".swiper-pagination",clickable:!0},breakpoints:{768:{slidesPerView:"auto",centeredSlides:!0,spaceBetween:12,slidesOffsetBefore:0}}})};