var enableScroll=function(e){var n=document.querySelector("#scroller"),o=document.querySelector(e);n.onclick=function(){o.scrollIntoView({behavior:"smooth"})}},enableSwiper=function(){return new Swiper(".swiper-container",{loop:!0,centeredSlides:!0,spaceBetween:8,slidesPerView:"auto",pagination:{el:".swiper-pagination"}})};window.onload=function(){enableScroll("#home-testimonials"),enableSwiper()};